/*
 Realizar un programa que muestre el uso del operador relacional. Pruebe al menos 5ejemplos.
 */
public class ejercicio11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int cinco = 5;
		String palabra = "Hola";
		String palabra2 = "Mundo";
		boolean logico = false;
		int entero = 10;
		
		System.out.println("Iguales: " + (cinco == entero));
		System.out.println("Distintos : "+(palabra != palabra2));
		System.out.println("Mayor: "+(entero>cinco));
		System.out.println("Menor: "+(cinco<entero));
		System.out.println("Negacion: "+ !logico);
		
		
		System.out.println("iguales  ==");
	}

}
